class LittlePrince {
    constructor() {
        this.meetPilot();
    }

    meetPilot() {
        // The little prince asks the pilot to draw a sheep
        let sheepDrawing = this.requestSheepDrawing();

        // If the sheep drawing is not suitable, draw a sheep in a box
        if (!sheepDrawing) {
            this.drawSheepInBox();
        }
    }

    requestSheepDrawing() {
        // The little prince rejects the first sheep because it is sick
        if (!this.isSuitableSheep("sick sheep")) return false;

        // The little prince rejects the second sheep because it is too old
        if (!this.isSuitableSheep("old sheep")) return false;

        // The little prince rejects the third sheep because it is too small
        if (!this.isSuitableSheep("small sheep")) return false;

        return true;
    }

    isSuitableSheep(description) {
        // None of the sheep drawn are suitable
        return false;
    }

    drawSheepInBox() {
        // The pilot draws a sheep in a box
        let sheepInBox = 'sheep inside the box';
        return sheepInBox;
    }
}

// Test execution
const prince = new LittlePrince();
